mapper.insert:
insert into subsidy (companyId,userId,subsidyFare,subsidyVersion,invalidDate,subsidyStatus) select #{companyId},userId,0,#{subsidyVersion}, #{invalidDate},0 from (
select #{item} userId
) t where t.userId not in (select userId from subsidy where companyId=#{companyId})

mapper.delete:
		delete from Subsidy where id in (		
		  
    		(#{item})
		
		)
